sys_script_include.script:
var CodefreshLogger = Class.create();
CodefreshLogger.prototype = {
    initialize: function() {
    },

    type: 'CodefreshLogger'
};

CodefreshLogger.Codefresh_DEBUG_PROPERTY = 'x_409723_codefresh.enable_debug';

CodefreshLogger.isLoggingEnabled = function () {
    var debugValue = gs.getProperty('x_409723_codefresh.enable_debug', 'false');
    if (debugValue && debugValue == 'true')
        return true;
    return false;
};

CodefreshLogger.isSessionDebug = function () {
    var debugValue = GlideSessionDebug.isDebugging('log');
    if (debugValue)
        return true;
    return false;
};

CodefreshLogger.log = function(msg) {
    if (/*CodefreshLogger.isSessionDebug() ||*/ CodefreshLogger.isLoggingEnabled()) {
        gs.info(msg, "Codefresh");
    }  
};
