sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var result;
	var version = 'v1';

	var payload = request.body.data;

	if (! payload.cf_build_id) {
		response.setBody(CodefreshErrorResponse.MISSING_BUILD_ID);
		response.setStatus(404);
		return;
	} 

	try {
		var inputs = {};
		inputs['cf_build_id'] = payload.cf_build_id; // String 
		delete payload[cf_build_id];
		input['fields']= payload;

		// Start Asynchronously: Uncomment to run in background. Code snippet will not have access to outputs.
		sn_fd.FlowAPI.getRunner().subflow('x_409723_codefresh.createchange').inBackground().withInputs(inputs).run();

		// Execute Synchronously: Run in foreground. Code snippet has access to outputs.
		// var result = sn_fd.FlowAPI.getRunner().subflow('x_409723_codefresh.createchange').inForeground().withInputs(inputs).run();
		// var outputs = result.getOutputs();

		// Current subflow has no outputs defined.		
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
		response.setBody(CodefreshErrorResponse.UNABLE_TO_PROCESS_ERROR);
		response.setStatus (400);
		return;
	}	
	CodefreshLogger.log("Codefresh Scripted REST API (createChange) -> Flow started with BUILD_ID" + payload.cf_build_id);


})(request, response);
